import React from 'react';
import Button from '../../UI/Button/Button';

const OrderSummary = (props) => {
    const ingredientSummary = Object.keys(props.ingredients)
        .map(igKey => {
            return (
                <li key={igKey}>
                    <span style={{ textTransform: 'capitalize' }}>{igKey}</span>: {props.ingredients[igKey]}
                </li>
            )
        });
    return (
        <>
            <h3>Your orded</h3>
            <p>A delicious burger with folowing ingredients:</p>
            <ul>
                {ingredientSummary}
            </ul>
            <p>Total price is: <strong>{props.price.toFixed(2)}</strong> dinara!</p>
            <p>Continue to checkout?</p>
            <Button buttonType="Danger" clicked={props.purchaseCanceled}>CANCEL</Button>
            <Button buttonType="Success" clicked={props.purchaseContinue}>CONTINUE</Button>
        </>
    );
};

export default OrderSummary;
