import React, { Component } from 'react';
import logo from './logo.svg';
import classes from './App.module.css';
import Person from '../Components/Persons/Person/Person';
// import Radium, {StyleRoot} from 'radium';
import ErrorBoundary from '../Components/ErrorBoundary/ErrorBoundary';



class App extends Component {
  state = {
    persons: [
      { id: 'asda', name: "Nico", age: 23 },
      { id: 'asdga', name: "Don", age: 53 },
      { id: '124ds', name: "Lol", age: 43 },
      { id: 'sdkjnfkl', name: "Heh", age: 33 },
    ],
    otherState: 'Nesto drugo',
    showPersons: false
  }

  deletePersonHandler = (personIndex) => {
    const persons = [...this.state.persons];
    persons.splice(personIndex, 1);
    this.setState({ persons: persons });
  }

  nameChangedHandler = (event, id) => {
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id;
    });

    const person = {
      ...this.state.persons[personIndex]
    };

    // const person = Object.assign({}, this.state.persons[personIndex]);

    person.name = event.target.value;

    const persons = [...this.state.persons];
    persons[personIndex] = person;

    this.setState({ persons: persons });
  }

  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({ showPersons: !doesShow });
  }

  render() {

    let persons = null;
    let btnClass = '';

    if (this.state.showPersons) {
      persons = (
        <div>
          {this.state.persons.map((person, index) => {
            return <Person
              click={() => this.deletePersonHandler(index)}
              name={person.name}
              age={person.age}
              key={person.id}
              changed={(event) => this.nameChangedHandler(event, person.id)} />
          })}
        </div>
      );

      btnClass = classes.Red
    }

    let assogmedClasses = [];
    if (this.state.persons.length <= 2) {
      assogmedClasses.push(classes.red);
    }
    if (this.state.persons.length <= 1) {
      assogmedClasses.push(classes.bold);
    }

    return (
      <div className={classes.App}>
        <h1>Hi, i'm starting up.</h1>
        <p className={assogmedClasses.join(' ')}>lolcina</p>
        <button className={btnClass}
          onClick={this.togglePersonsHandler}>Toggle persons</button>
        {persons}
      </div>
    );
  }
}

export default App;
