import logo from './logo.svg';
import './App.css';
import Person from './Person/Person';
import React, { Component } from 'react';

class App extends Component {
  state = {
    persons: [
      { name: "Nico", age: 23 },
      { name: "Don", age: 53 },
      { name: "Lol", age: 43 },
      { name: "Heh", age: 33 },
    ]
  }

  switchNameHandler = () => {
    // console.log("was clicked!");
    // this.state.persons[0].name = "Nikolaj";
    this.setState({
      persons: [
        { name: "Nico", age: 23 },
        { name: "Don", age: 53 },
        { name: "Lol", age: 43 },
        { name: "Heh", age: 33 },
      ]
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Hi , im starting up.</h1>
        <h1>lolcina</h1>
        <button onClick={this.switchNameHandler}>Switch name</button>
        <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
        <Person name={this.state.persons[1].name} age={this.state.persons[1].age} />
        <Person name={this.state.persons[2].name} age={this.state.persons[2].age} />
        <Person name={this.state.persons[3].name} age={this.state.persons[3].age}>Moj hobi je ojsaaa!</Person>
      </div>
    );
  }
}

export default App;
