import logo from './logo.svg';
import './App.css';
import Person from './Person/Person';

import React, { Component } from 'react';

class App extends Component {
  state = {
    persons: [
      { name: "Nico", age: 23 },
      { name: "Don", age: 53 },
      { name: "Lol", age: 43 },
      { name: "Heh", age: 33 },
    ],
    otherState: 'Nesto drugo',
    showPersons: false
  }

  deletePersonHandler = () => {

  }

  nameChangedHadnelr = (event) => {
    this.setState({
      persons: [
        { name: "Nikolaj", age: 23 },
        { name: "Don", age: 53 },
        { name: event.target.value, age: 43 },
        { name: "Heh", age: 69 },
      ]
    })
  }

  togglePersonsHandler = () =>{
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  }

  render() {
    const style = {
      barckbroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    let persons = null;

    if (this.state.showPersons) {
      persons = (
        <div>
          {this.state.persons.map((person, index) => {
            return <Person 
              click = {() => this.deletePersonHandler(index)} name ={person.name} age = {person.age}/>
          })}
        </div>
      );
    }

    return (
      <div className="App">
        <h1>Hi, i'm starting up.</h1>
        <h1>lolcina</h1>
        <button style={style}
          onClick={this.togglePersonsHandler}>Switch name</button>
          {persons}   
      </div>
    );
  }
}

export default App;
